{
    "resourceId": "string"
}

// В ответе на запрос приходит поле resourceId — идентификатор почтового ящика, для которого включена возможность делегирования. Для делегированных ящиков идентификатор почтового ящика совпадает с идентификатором сотрудника-владельца этого ящика.

// +-------------+--------------------------------+
// | Name        | Description                    |
// +=============+================================+
// | resourceId* | Type: string<uint64>           |
// |             | Идентификатор почтового ящика. |
// +-------------+--------------------------------+

{
    "roles": [
        "string"
    ]
}

// For examlple:
// {
//     "roles": [
//         "shared_mailbox_imap_admin",
//         "shared_mailbox_half_sender"
//     ]
// }

// +--------+------------------------------------------------------------------------------------------------------------------------------+
// | Name   | Description                                                                                                                  |
// +========+==============================================================================================================================+
// | roles* | Type: string[]                                                                                                               |
// |        | Список ролей, на которые назначается сотрудник.                                                                              |
// |        | Возможные значения:                                                                                                          |
// |        | - shared_mailbox_owner                                                                                                       |
// |        |     полные права на ящик, к которому предоставляется доступ;                                                                 |
// |        | - shared_mailbox_imap_admin                                                                                                  |
// |        |     управление ящиком в IMAP-клиенте;                                                                                        |
// |        | - shared_mailbox_sender                                                                                                      |
// |        |     отправка писем по SMTP;                                                                                                  |
// |        | - shared_mailbox_half_sender                                                                                                 |
// |        |     ограниченная отправка писем по SMTP.                                                                                     |
// |        | Роль shared_mailbox_owner включает в себя права всех остальных ролей. При выборе этой роли указывать остальные не требуется. |
// +--------+------------------------------------------------------------------------------------------------------------------------------+

{
    "name": "string",
    "description": "string"
}

// For examle:
// {
//     "description": "Ящик отдела продаж"
// }

// +-------------+--------------+
// | Name        | Description  |
// +=============+==============+
// | description | Type: string |
// |             | Описание.    |
// +-------------+--------------+
// | name        | Type: string |
// |             | Имя.         |
// +-------------+--------------+

{
    "firstName": "string",
    "lastName": "string",
    "middleName": "string",
    "title": "string",
    "company": "string",
    "department": "string",
    "address": "string"
}

// +------------+-----------------+
// | Name       | Description     |
// +============+=================+
// | address    | Type: string    |
// |            | Почтовый адрес. |
// +------------+-----------------+
// | company    | Type: string    |
// |            | Компания.       |
// +------------+-----------------+
// | department | Type: string    |
// |            | Подразделение.  |
// +------------+-----------------+
// | firstName  | Type: string    |
// |            | Имя.            |
// +------------+-----------------+
// | lastName   | Type: string    |
// |            | Фамилия.        |
// +------------+-----------------+
// | middleName | Type: string    |
// |            | Отчество.       |
// +------------+-----------------+
// | title      | Type: string    |
// |            | Должность.      |
// +------------+-----------------+

{
    "type": "user",
    "id": "string"
}

// +-------+----------------------------------+
// | Name  | Description                      |
// +=======+==================================+
// | id*   | Type: string<uint64>             |
// |       | Идентификатор участника группы.  |
// +-------+----------------------------------+
// | type* | Type: GroupMemberGroupMemberType |
// |       | Тип участника группы.            |
// |       | Default: user                    |
// |       | Enum: user, group, department    |
// +-------+----------------------------------+

// GroupMemberGroupMemberType
// Тип участника группы.
//   user: Сотрудник.
//   group: Группа.
//   department: Подразделение.
// +----------------------------+-------------------------------+
// | Type                       | Description                   |
// +============================+===============================+
// | GroupMemberGroupMemberType | Default: user                 |
// |                            | Enum: user, group, department |
// +----------------------------+-------------------------------+

{
    "duration": 0,
    "logoutUsers": false,
    "validationMethod": "default"
}

// For examle:
// {
//     "duration": 1209600,
//     "validationMethod": "phone"
// }

// +------------------+-----------------------------------------------------------------------------------------------------------------------------------------+
// | Name             | Description                                                                                                                             |
// +==================+=========================================================================================================================================+
// | duration*        | Type: integer<int64>                                                                                                                    |
// |                  | Период (в секундах), в течение которого у пользователя в процессе авторизации есть возможность отложить настройку 2FA.                  |
// |                  | По истечении этого периода возможность отложить настройку отключается.                                                                  |
// +------------------+-----------------------------------------------------------------------------------------------------------------------------------------+
// | logoutUsers      | Type: boolean                                                                                                                           |
// |                  | Признак, что всех пользователей домена нужно единоразово разлогинить при включении 2FA.                                                 |
// |                  | Если использовались пароли приложений, потребуется создать их заново.                                                                   |
// +------------------+-----------------------------------------------------------------------------------------------------------------------------------------+
// | validationMethod | Type: string                                                                                                                            |
// |                  | Метод, которым будет проверяться второй фактор:                                                                                         |
// |                  |   default — использовать метод проверки, заданный в алгоритмах Яндекс ID в качестве приоритетного.                                      |
// |                  |   phone — определять итоговый метод проверки с помощью алгоритмов Яндекс ID, но отдавать по возможности предпочтение звонку на телефон. |
// |                  | Default: default                                                                                                                        |
// |                  | Enum: default, phone                                                                                                                    |
// +------------------+-----------------------------------------------------------------------------------------------------------------------------------------+

{
    "applications": [
        {
            "id": "string",
            "scopes": [
                "string"
            ]
        }
    ]
}

// For examle:
// {
//   "applications": [
//     {
//       "id": "service_application_id1",
//       "scopes": [
//         "cloud_api:disk.app_folder",
//         "cloud_api:disk.read",
//         "cloud_api:disk.write",
//         "cloud_api:disk.info"
//       ]
//     },
//     {
//       "id": "service_application_id2",
//       "scopes": [
//         "calendar:all"
//       ]
//     },
//     {
//       "id": "service_application_id3",
//       "scopes": [
//         "mail:imap_full",
//         "mail:imap_ro",
//         "mail:smtp"
//       ]
//     }
//   ]
// }

// +---------------+------------------------------+
// | Name          | Description                  |
// +===============+==============================+
// | applications* | Type: v1ServiceApplication[] |
// |               | Список сервисных приложений. |
// +---------------+------------------------------+

// v1ServiceApplication
// +---------+-----------------------------+
// | Name    | Description                 |
// +=========+=============================+
// | id*     | Type: string                |
// |         | Идентификатор приложения.   |
// +---------+-----------------------------+
// | scopes* | Type: string[]              |
// |         | Список доступов приложения. |
// +---------+-----------------------------+

// Список прав доступа, которые могут использоваться в сервисных приложениях:
// cloud_api:disk.app_folder,
// cloud_api:disk.read,
// cloud_api:disk.write,
// cloud_api:disk.info,
// yadisk:disk,
// calendar:all,
// mail:imap_full,
// mail:imap_ro,
// mail:smtp,
// telemost-api:conferences.create,
// telemost-api:conferences.read,
// telemost-api:conferences.update,
// telemost-api:conferences.delete.
